import axios from "axios";
import { useState } from "react";

const AddEmployee = () => {
    const backendUrl = 'https://jsonplaceholder.typicode.com/users';
    const [empData, setEmpData] = useState({ firstName: '', email: '', aadhaar: '', salary: '' });
    const [errors, setErrors] = useState({});

    const handleChange = (evt) => {
        setEmpData({ ...empData, [evt.target.name]: evt.target.value });
        setErrors({ ...errors, [evt.target.name]: '' });
    };

    const validateForm = () => {
        let isValid = true;
        const newErrors = {};

        if (!empData.firstName.trim()) {
            newErrors.firstName = "First name is required";
            isValid = false;
        }

        if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(empData.email)) {
            newErrors.email = "Invalid email address";
            isValid = false;
        }

        if (!/^\d{12}$/.test(empData.aadhaar)) {
            newErrors.aadhaar = "Aadhaar must be a 12-digit number";
            isValid = false;
        }

        if (empData.salary <= 0 || isNaN(empData.salary)) {
            newErrors.salary = "Salary must be a positive number";
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };

    const handleSubmit = (evt) => {
        evt.preventDefault();
        if (validateForm()) {
            axios.post(backendUrl, empData)
                .then((resp) => {
                    alert(`${resp.data.firstName} with id ${resp.data.id} added successfully!`);
                    setEmpData({ firstName: '', email: '', aadhaar: '', salary: '' });
                })
                .catch(error => {
                    console.error("Error adding employee:", error);
                });
        }
    };

    return (
        <>
            <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Add Employee</h1>
<form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
    <label htmlFor="firstName" style={{ marginBottom: '10px' }}>First Name:</label>
    <input type="text" id="firstName" name="firstName" value={empData.firstName} onChange={handleChange} placeholder="Enter first name" required autoFocus style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
    {errors.firstName && <span className="error" style={{ color: 'red', marginBottom: '10px' }}>{errors.firstName}</span>}
    <label htmlFor="email" style={{ marginBottom: '10px' }}>Email:</label>
    <input type="email" id="email" name="email" value={empData.email} onChange={handleChange} placeholder="Enter email" style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
    {errors.email && <span className="error" style={{ color: 'red', marginBottom: '10px' }}>{errors.email}</span>}
    <label htmlFor="aadhaar" style={{ marginBottom: '10px' }}>Aadhaar:</label>
    <input type="number" id="aadhaar" name="aadhaar" value={empData.aadhaar} onChange={handleChange} placeholder="Enter aadhaar" style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
    {errors.aadhaar && <span className="error" style={{ color: 'red', marginBottom: '10px' }}>{errors.aadhaar}</span>}
    <label htmlFor="salary" style={{ marginBottom: '10px' }}>Salary:</label>
    <input type="number" id="salary" name="salary" value={empData.salary} onChange={handleChange} placeholder="Enter salary" style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
    {errors.salary && <span className="error" style={{ color: 'red', marginBottom: '10px' }}>{errors.salary}</span>}
    <input type="submit" value="Add Employee" style={{ backgroundColor: '#4CAF50', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px' }} />
</form>
        </>
    );
};

export default AddEmployee;

