import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateEmployee = ({ employees, fetchEmployees }) => {
    const [selectedEmployeeId, setSelectedEmployeeId] = useState("");
    const [updatedEmployee, setUpdatedEmployee] = useState({
      id: "",
      firstname: "",
      lastname: "",
      email: "",
      salary: ""
    });
  
    useEffect(() => {
      if (selectedEmployeeId && employees) {
        const selectedEmployee = employees.find(emp => emp.id === selectedEmployeeId);
        if (selectedEmployee) {
          setUpdatedEmployee(selectedEmployee);
        }
      }
    }, [selectedEmployeeId, employees]);
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setUpdatedEmployee((prevEmployee) => ({
        ...prevEmployee,
        [name]: value
      }));
    };
  
    const handleSelectChange = (e) => {
      setSelectedEmployeeId(e.target.value);
    };
  
    const handleSubmit = (e) => {
      e.preventDefault();
      axios
        .put(`http://localhost:8080/emp/update-emp/${updatedEmployee.id}`, updatedEmployee)
        .then(() => {
          alert("Employee updated successfully!");
          fetchEmployees();
          setSelectedEmployeeId("");
        })
        .catch((error) => {
          console.error("Error updating employee:", error);
        });
    };
  
    return (
      <div>
        <h2>Update Employee</h2>
        <label>
          Select Employee:
          <select value={selectedEmployeeId} onChange={handleSelectChange}>
            <option value="">Select Employee</option>
            {employees && employees.map(employee => (
              <option key={employee.id} value={employee.id}>{employee.firstname} {employee.lastname}</option>
            ))}
          </select>
        </label>
        {selectedEmployeeId && (
          <form onSubmit={handleSubmit}>
            <label>
              First Name:
              <input
                type="text"
                name="firstname"
                value={updatedEmployee.firstname}
                onChange={handleChange}
                required
              />
            </label>
            <br />
            {/* Other form fields */}
            <button type="submit">Update Employee</button>
          </form>
        )}
      </div>
    );
  };
  
  export default UpdateEmployee;
