import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateEmployee = () => {
  const [empList, setEmpList] = useState([]);
  const [updateData, setUpdateData] = useState({
    id: '',
    name: '',
    username: '',
    email: '',
    city: ''
  });

  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        setEmpList(response.data);
      })
      .catch(error => {
        console.error('Error fetching employees:', error);
      });
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUpdateData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // You can send an HTTP request to update the employee data
    // For demonstration purpose, let's just log the updated data
    console.log("Updated data:", updateData);
  };

  return (
    <div>
      <h1>Update Employee</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Select Employee:
          <select name="id" onChange={handleInputChange} value={updateData.id}>
            <option value="">Select an employee</option>
            {empList.map(employee => (
              <option key={employee.id} value={employee.id}>{employee.name}</option>
            ))}
          </select>
        </label>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={updateData.name} onChange={handleInputChange} />
        </div>
        <div>
          <label>Username:</label>
          <input type="text" name="username" value={updateData.username} onChange={handleInputChange} />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={updateData.email} onChange={handleInputChange} />
        </div>
        <div>
          <label>City:</label>
          <input type="text" name="city" value={updateData.city} onChange={handleInputChange} />
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default UpdateEmployee;
