import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateEmployee = ({ employee, fetchEmployees }) => {
    const [updatedEmployee, setUpdatedEmployee] = useState(employee);
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setUpdatedEmployee((prevEmployee) => ({
        ...prevEmployee,
        [name]: value
      }));
    };
  
    const handleSubmit = (e) => {
      e.preventDefault();
      axios
        .put(`http://localhost:8080/emp/update-emp/${updatedEmployee.id}`, updatedEmployee)
        .then(() => {
          alert("Employee updated successfully!");
          fetchEmployees();
        })
        .catch((error) => {
          console.error("Error updating employee:", error);
        });
    };
  
    return (
      <div>
        <h2>Update Employee</h2>
        <form onSubmit={handleSubmit}>
          <label>
            First Name:
            <input
              type="text"
              name="firstname"
              value={updatedEmployee.firstname}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <label>
            Last Name:
            <input
              type="text"
              name="lastname"
              value={updatedEmployee.lastname}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={updatedEmployee.email}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <label>
            Salary:
            <input
              type="number"
              name="salary"
              value={updatedEmployee.salary}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <button type="submit">Update Employee</button>
        </form>
      </div>
    );
  };
  
export default UpdateEmployee;
