import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DeleteEmployee = () => {
  const [empList, setEmpList] = useState([]);
  const [selectedEmployeeId, setSelectedEmployeeId] = useState('');

  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        setEmpList(response.data);
      })
      .catch(error => {
        console.error('Error fetching employees:', error);
      });
  }, []);

  const handleDelete = () => {
    // You can send an HTTP request to delete the selected employee
    // For demonstration purpose, let's just filter out the selected employee from the list
    const updatedList = empList.filter(emp => emp.id !== selectedEmployeeId);
    setEmpList(updatedList);
    setSelectedEmployeeId('');
  };

  return (
    <div>
      <h1>Delete Employee</h1>
      <label>
        Select Employee:
        <select value={selectedEmployeeId} onChange={(e) => setSelectedEmployeeId(e.target.value)}>
          <option value="">Select an employee</option>
          {empList.map(employee => (
            <option key={employee.id} value={employee.id}>{employee.name}</option>
          ))}
        </select>
      </label>
      <button onClick={handleDelete} disabled={!selectedEmployeeId}>Delete</button>
    </div>
  );
};

export default DeleteEmployee;
