import axios from "axios";
import { useState } from "react";

const AddEmployee = () => {
    const backendUrl = 'localhost:8080/emp/add-emp';
    const [empData, setEmpData] = useState({ employeeId: '', firstname: '', salary: '', lastname: '' });
    const [errors, setErrors] = useState({});

    const handleChange = (evt) => {
        setEmpData({ ...empData, [evt.target.name]: evt.target.value });
        setErrors({ ...errors, [evt.target.name]: '' });
    };

    const validateForm = () => {
        let isValid = true;
        const newErrors = {};

        if (!empData.employeeId.trim()) {
            newErrors.employeeId = "First name is required";
            isValid = false;
        }

        if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(empData.firstname)) {
            newErrors.firstname = "Invalid email address";
            isValid = false;
        }

        if (!/^\d{12}$/.test(empData.salary)) {
            newErrors.salary = "Aadhaar must be a 12-digit number";
            isValid = false;
        }

        if (empData.salary <= 0 || isNaN(empData.salary)) {
            newErrors.salary = "Salary must be a positive number";
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };

    const handleSubmit = (evt) => {
        evt.preventDefault();
        if (validateForm()) {
            axios.post(backendUrl, empData)
                .then((resp) => {
                    alert(`${resp.data.firstname} with id ${resp.data.employeeIdId} added successfully!`);
                    setEmpData({ employeeId: '', firstname: '', salary: '', lastname: '' });
                })
                .catch(error => {
                    console.error("Error adding employee:", error);
                });
        }
    };

    return (
        <>
            <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Add Employee</h1>
<form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
   
    <label htmlFor="employeeId" style={{ marginBottom: '10px' }}>Employee ID:</label>
    <input type="text" id="employeeId" name="employeeId" value={empData.employeeId} onChange={handleChange} placeholder="Enter Employee Id" required autoFocus style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
    {errors.firstName && <span className="error" style={{ color: 'red', marginBottom: '10px' }}>{errors.firstName}</span>}
    
    <label htmlFor="firstname" style={{ marginBottom: '10px' }}>First Name:</label>
    <input type="firstname" id="firstname" name="firstname" value={empData.firstname} onChange={handleChange} placeholder="Enter First Name" style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
    {errors.email && <span className="error" style={{ color: 'red', marginBottom: '10px' }}>{errors.email}</span>}
    
    <label htmlFor="lastname" style={{ marginBottom: '10px' }}>lastName:</label>
    <input type="lastname" id="lastname" name="lastname" value={empData.lastname} onChange={handleChange} placeholder="Enter Last Name" style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
    {errors.aadhaar && <span className="error" style={{ color: 'red', marginBottom: '10px' }}>{errors.lastname}</span>}
    
    <label htmlFor="salary" style={{ marginBottom: '10px' }}>Salary:</label>
    <input type="number" id="salary" name="salary" value={empData.salary} onChange={handleChange} placeholder="Enter salary" style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
    {errors.salary && <span className="error" style={{ color: 'red', marginBottom: '10px' }}>{errors.salary}</span>}
    
    <input type="submit" value="Add Employee" style={{ backgroundColor: '#4CAF50', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px' }} />
</form>
        </>
    );
};

export default AddEmployee;

